import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CourseSelector = () => {
  const [courses, setCourses] = useState([]);
  const [selectedCourses, setSelectedCourses] = useState([]);

  useEffect(() => {
    // Fetch the courses from the API
    axios.get('http://localhost:5000/api/college-posts')
      .then(response => setCourses(response.data))
      .catch(error => console.error('Error fetching courses:', error));
  }, []);

  const handleSelect = (courseId) => {
    setSelectedCourses(prevSelected => 
      prevSelected.includes(courseId) 
        ? prevSelected.filter(id => id !== courseId)
        : [...prevSelected, courseId]
    );
  };

  const handleRemove = () => {
    const updatedCourses = courses.filter(course => !selectedCourses.includes(course.id));
    setCourses(updatedCourses);

    // Optionally update the file (or backend) with the updated course list
    axios.post('http://localhost:5000/api/college-posts', updatedCourses)
      .then(response => console.log('Courses updated successfully'))
      .catch(error => console.error('Error updating courses:', error));

    // Clear the selected courses
    setSelectedCourses([]);
  };

  return (
    <div>
      <h2>Select and Remove Courses</h2>
      <ul>
        {courses.map(course => (
          <li key={course.id}>
            <input
              type="checkbox"
              checked={selectedCourses.includes(course.id)}
              onChange={() => handleSelect(course.id)}
            />
            {course.name}
          </li>
        ))}
      </ul>
      <button onClick={handleRemove} disabled={selectedCourses.length === 0}>
        Remove Selected Courses
      </button>
    </div>
  );
};

export default CourseSelector;
