import React from 'react';

const BillingPage = ({ cart, onRemoveFromCart, onBuyNow }) => {
  const totalCost = cart.reduce((total, course) => total + course.cost, 0);

  return (
    <div className="billing-page">
      <h2>Your Cart</h2>
      <div className="cart-items">
        {cart.length > 0 ? (
          cart.map((course) => (
            <div key={course.id} className="cart-item">
              <span>{course.title}</span>
              <span>${course.cost}</span>
              <button onClick={() => onRemoveFromCart(course.id)}>Remove</button>
            </div>
          ))
        ) : (
          <p>Your cart is empty</p>
        )}
      </div>
      <div className="billing-summary">
        <h3>Total: ${totalCost}</h3>
        <button onClick={onBuyNow} disabled={cart.length === 0}>
          Buy Now
        </button>
      </div>
    </div>
  );
};

export default BillingPage;
