import React, { useEffect, useState } from 'react';

const CourseList = () => {
    const [courses, setCourses] = useState([]);

    useEffect(() => {
        fetch('http://localhost:5000/api/courses-admin')
            .then(res => res.json())
            .then(data => setCourses(data))
            .catch(err => console.error(err));
    }, []);

    const handleSubmit = (event) => {
        event.preventDefault();

        const formData = new FormData(event.target);
        const updatedCourses = courses.map(course => ({
            ...course,
            status: formData.get(`status-${course.id}`) || course.status,
        }));

        fetch('http://localhost:5000/api/courses-admin', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(updatedCourses),
        })
        .then(res => res.text())
        .then(data => {
            setCourses(updatedCourses);
            alert(data);
        })
        .catch(err => console.error(err));
    };

    return (
        <form onSubmit={handleSubmit}>
            {courses.map(course => (
                <div key={course.id}>
                    <h2>{course.name}</h2>
                    <p>{course.description}</p>
                    <label>
                        <input
                            type="radio"
                            name={`status-${course.id}`}
                            value="verified"
                            defaultChecked={course.status === 'verified'}
                        />
                        Verified
                    </label>
                    <label>
                        <input
                            type="radio"
                            name={`status-${course.id}`}
                            value="unverified"
                            defaultChecked={course.status === 'unverified'}
                        />
                        Unverified
                    </label>
                </div>
            ))}
            <button type="submit">Submit</button>
        </form>
    );
};

export default CourseList;
