// src/components/UserInfo.js
import React, { useState, useEffect } from 'react';
import Posted from './Posted.js';
import Sold from './Sold.js';

const UserInfo = ({ user }) => {
  const [posts, setPosts] = useState([]);
  const [purchases, setPurchases] = useState([]);
  
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/college-posts');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };
    
    const fetchPurchases = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/college-courses-sold?userId=${user._id}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setPurchases(data);
      } catch (error) {
        console.error('Error fetching purchases:', error);
      }
    };
    if (user && user._id) {
      fetchPosts();
      fetchPurchases();
    }
  }, [user]);
  
  return (
    <div>
      <h2>Welcome {user.name}</h2>
      <p>College: {user.college}</p>
      <p>Email: {user.email}</p>
	   <div className="posts">
            <Posted
              postedCourses={posts}
            />
            <br></br>
            <Sold
              Sold={purchases}
            />
        </div>
    </div>
  );
};

export default UserInfo;
