import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import UserInfo from './components/UserInfo';
import Filters from './components/Filters';
import CourseCard from './components/CourseCard';
import BillingPage from './components/BillingPage';
import FixedBottom from './components/FixedBottom';
import './App.css';

const App = () => {
  const [user, setUser] = useState(null); // Initialize user to null
  const [courses, setCourses] = useState([]);
  const [filters, setFilters] = useState({ branches: [], districts: [] });
  const [selectedBranches, setSelectedBranches] = useState([]);
  const [selectedDistricts, setSelectedDistricts] = useState([]);
  const [cart, setCart] = useState([]);
  const [purchases, setPurchases] = useState([]);

  // Fetch user data from localStorage or API
  useEffect(() => {
    const fetchUser = async () => {
      const storedUser = localStorage.getItem('user');
      if (storedUser) {
        setUser(JSON.parse(storedUser)); // Set user from localStorage
      } else {
        try {
          const response = await fetch('http://localhost:5000/api/user');
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          const data = await response.json();
          console.log(data);
          setUser(data);
          localStorage.setItem('user', JSON.stringify(data)); // Save user in localStorage
        } catch (error) {
          console.error('Error fetching user:', error);
        }
      }
    };

    const fetchFilters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/filters');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setFilters(data);
      } catch (error) {
        console.error('Error fetching filters:', error);
      }
    };

    const fetchCourses = async (userCourse) => {
      try {
        const response = await fetch(`http://localhost:5000/api/courses?degree=${userCourse}`);
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setCourses(data);
      } catch (error) {
        console.error('Error fetching courses:', error);
      }
    };

    const fetchCart = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/cart');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setCart(data);
      } catch (error) {
        console.error('Error fetching cart:', error);
      }
    };

    const fetchPurchases = async (userId) => {
      try {
        const response = await fetch('http://localhost:5000/api/purchases');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setPurchases(data);
      } catch (error) {
        console.error('Error fetching purchases:', error);
      }
    };

    fetchUser();
    fetchFilters();
    if (user) {
      fetchCourses(user.course);
      fetchCart();
      fetchPurchases(user.id);
    }
  }, [user]);

  const fetchCoursesWithFilters = async (branches, districts, usercourse) => {
    try {
      const branchQuery = branches.join(',');
      const distQuery = districts.join(',');
      const response = await fetch(`http://localhost:5000/api/courses?degree=${usercourse}&branch=${branchQuery}&district=${distQuery}`);
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      setCourses(data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    }
  };

  const handleApplyFilters = (branches, districts) => {
    setSelectedBranches(branches);
    setSelectedDistricts(districts);
    fetchCoursesWithFilters(branches, districts, user.course);
  };

  const handleResetFilters = () => {
    setSelectedBranches([]);
    setSelectedDistricts([]);
    fetchCoursesWithFilters([], [], user.course);
  };

  const handleAddToCart = async (course) => {
    try {
      const response = await fetch('http://localhost:5000/api/cart', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ course }),
      });
      const updatedCart = await response.json();
      setCart(updatedCart);
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  const handleRemoveFromCart = async (courseId) => {
    try {
      const response = await fetch('http://localhost:5000/api/cart', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ courseId }),
      });
      const updatedCart = await response.json();
      setCart(updatedCart);
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  const handleLogout = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/logout', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const updatedCart = await response.json();
      setCart(updatedCart);
      localStorage.removeItem('user');
      setUser(null);
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="app">
      <Link to="/">
        <button className="logout" onClick={handleLogout}>
          Logout
        </button>
      </Link>
      <div className="row user-info">
        <UserInfo user={user} />
      </div>
      <div className="row main-content">
        <div className="filters">
          <h3 id='fixedheading'>Apply Filters</h3>
          <Filters
            branches={filters.branches}
            districts={filters.districts}
            onApplyFilters={handleApplyFilters}
            onResetFilters={handleResetFilters}
          />
        </div>

        <div className="courses">
          {courses.map((course, index) => (
            <CourseCard
              key={index}
              course={course}
              isInCart={cart.some(item => item.id === course.id)}
              isInPurchases={purchases.some(item => item.user.id === user.id && item.course.id === course.id)}
              onBuyNow={() => console.log('Buying:', course)}
              onAddToCart={() => handleAddToCart(course)}
              onRemoveFromCart={() => handleRemoveFromCart(course.id)}
            />
          ))}
        </div>
      </div>
      <div className="fixed-bottom">
        <FixedBottom cartCount={cart.length} />
      </div>
    </div>
  );
};

export default App;
