import React, { useState } from 'react';

const Filters = ({ branches, districts, onApplyFilters, onResetFilters }) => {
  const [selectedBranches, setSelectedBranches] = useState([]);
  const [selectedDistricts, setSelectedDistricts] = useState([]);

  const handleBranchChange = (e) => {
    const value = e.target.value;
    setSelectedBranches(prevState =>
      prevState.includes(value)
        ? prevState.filter(branch => branch !== value)
        : [...prevState, value]
    );
  };

  const handleDistrictChange = (e) => {
    setSelectedDistrict(e.target.value);
    const value = e.target.value;
    setSelectedDistricts(prevState =>
      prevState.includes(value)
        ? prevState.filter(district => district !== value)
        : [...prevState, value]
    );
  };

  const handleApplyFilters = () => {
    onApplyFilters(selectedBranches, selectedDistrict);
  };

  const handleResetFilters = () => {
    setSelectedBranches([]);
    setSelectedDistrict('');
    onResetFilters();
  };

  return (
    <div className='filter'>
      <h2>Apply Filters</h2>
      <div>
        <label>Branches:</label>
        <div>
          {branches.map((branch, index) => (
            <div key={index}>
              <input
                type="checkbox"
                value={branch}
                checked={selectedBranches.includes(branch)}
                onChange={handleBranchChange}
              />
              <label>{branch}</label>
            </div>
          ))}
        </div>
      </div>
      <br></br><br></br>
       <div>
        <label>Districts:</label>
        {districts.map((district, index) => (
          <div key={index}>
            <input
              type="checkbox"
              value={district}
              checked={selectedDistricts.includes(district)}
              onChange={handleDistrictChange}
            />
            {district}
        </div>
        ))}
        <br></br><br></br>
     <button onClick={handleApplyFilters}>Apply Filters</button>
      <button onClick={handleResetFilters}>Reset Filters</button>
    </div>

    </div>
  );
};

export default Filters;
