// src/AdminDashboard.js
import React from 'react';
import { Link } from 'react-router-dom';
import './app3.css'; // Updated CSS file
import CourseList from './components/CourseList';
import UserInfo from './components/purchased-admin';
import VerificationButton from './components/VerificationButton'; 

const AdminDashboard = () => {
  const [user, setUser] = React.useState(null);
  const [purchases, setPurchases] = React.useState([]);

  React.useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/user');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error('Error fetching user:', error);
      }
    };

    const fetchPurchases = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/purchases-admin');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setPurchases(data);
      } catch (error) {
        console.error('Error fetching purchases:', error);
      }
    };

    fetchUser();
    fetchPurchases();
  }, []);

  if (!user) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="admin-dashboard">
      <header className="header">
        <h1>Admin Dashboard</h1>
        <Link to="/">
          <button className="logout-btn">Logout</button>
        </Link>
      </header>
      <main className="main-content">
        <section className="user-info-section">
          <UserInfo boughtCourses={purchases} />
        </section>
        <section className="course-list-section">
          <CourseList status='verified' />
        </section>
        <section className="verification-section">
          <Link to="/students">
            <VerificationButton />
          </Link>
        </section>
      </main>
    </div>
  );
};

export default AdminDashboard;
