import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";


const BillingPage = () => {
  const [cart, setCart] = useState([]);
  const totalCost = cart.reduce((total, course) => total + course.cost, 0);

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/cart');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setCart(data);
      } catch (error) {
        console.error('Error fetching cart:', error);
      }
    };
    fetchCart();  
  });

  const handleRemoveFromCart = async (courseId) => {
    try {
      const response = await fetch('http://localhost:5000/api/cart', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ courseId }),
      });
      const updatedCart = await response.json();
      setCart(updatedCart);
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  const handleBuyNow = () => {
    alert('Purchase successful!');
    setCart([]); // Clear the cart after purchase
  };

  return (
    <div className="billing-page">
      <h2>Your Cart</h2>
      <div className="cart-items">
        {cart.length > 0 ? (
          cart.map((course) => (
            <div key={course.id} className="cart-item">
              <span>{course.title}</span>
              <span>{course.id}</span>
              <span>${course.cost}</span>
              <button onClick={() => handleRemoveFromCart(course.id)}>Remove</button>
            </div>
          ))
        ) : (
          <p>Your cart is empty</p>
        )}
      </div>
      <div className="billing-summary">
        <h3>Total: ${totalCost}</h3>
        <button onClick={handleBuyNow} disabled={cart.length === 0}>
          Buy Now
        </button>
        <Link to="/">
      <button className="go-to-dashboard">
        back to dashboard
      </button></Link>
      </div>
    </div>
  );
};

export default BillingPage;
