import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginModal.css';

const LoginTypeSelector = ({ loginType, handleLoginTypeChange }) => (
  <div className="login-type">
    <label>
      <input
        type="radio"
        value="student"
        checked={loginType === 'student'}
        onChange={handleLoginTypeChange}
      />
      Student
    </label>
    <label>
      <input
        type="radio"
        value="college"
        checked={loginType === 'college'}
        onChange={handleLoginTypeChange}
      />
      College
    </label>
    <label>
      <input
        type="radio"
        value="admin"
        checked={loginType === 'admin'}
        onChange={handleLoginTypeChange}
      />
      Admin
    </label>
  </div>
);

const LoginForm = ({
  isRegistering,
  email,
  setEmail,
  password,
  setPassword,
  name,
  setName,
  course,
  setCourse,
  percentile,
  setPercentile,
  marksheet,
  setMarksheet,
  handleFormSubmit
}) => (
  <div className="login-form">
    {isRegistering && (
      <>
        <input
          type="text"
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Course"
          value={course}
          onChange={(e) => setCourse(e.target.value)}
        />
        <input
          type="number"
          placeholder="Percentile"
          value={percentile}
          onChange={(e) => setPercentile(e.target.value)}
        />
        <input
          type="file"
          accept=".pdf, .jpg, .png"
          onChange={(e) => setMarksheet(e.target.files[0])}
        />
      </>
    )}
    <input
      type="email"
      placeholder="Email"
      value={email}
      onChange={(e) => setEmail(e.target.value)}
    />
    <input
      type="password"
      placeholder="Password"
      value={password}
      onChange={(e) => setPassword(e.target.value)}
    />
    <button onClick={handleFormSubmit}>
      {isRegistering ? 'Register' : 'Login'}
    </button>
  </div>
);

const LoginModal = () => {
  const [loginType, setLoginType] = useState('student');
  const [isRegistering, setIsRegistering] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [course, setCourse] = useState('');
  const [percentile, setPercentile] = useState('');
  const [marksheet, setMarksheet] = useState(null);
  const navigate = useNavigate();

  const handleLoginTypeChange = (e) => setLoginType(e.target.value);

  const handleFormSubmit = async () => {
    if (isRegistering) {
      const formData = new FormData();
      formData.append('name', name);
      formData.append('email', email);
      formData.append('password', password);
      formData.append('loginType', loginType);
      formData.append('course', course);
      formData.append('percentile', percentile);
      if (marksheet) {
        formData.append('marksheet', marksheet);
      }

      try {
        const response = await fetch('http://localhost:5000/api/register', {
          method: 'POST',
          body: formData,
        });
        const data = await response.json();
        if (data.success) {
          alert('Registration successful!');
        } else {
          alert('Registration failed: ' + data.message);
        }
      } catch (error) {
        console.error('Error during registration:', error);
      }
    }

    const logindata = await loginUser({ email, password, loginType });
    if (logindata.success) {
      if (logindata.user.type === loginType) {
        if (logindata.user.type === 'college') {
          navigate('/college-dashboard', { replace: true });
        } else if (logindata.user.type === 'admin') {
          navigate('/admin-dashboard', { replace: true });
        } else {
          navigate('/dashboard', { replace: true });
        }
      } else {
        alert('Login failed!');
      }
    } else {
      alert('Login failed!');
    }
  };

  const loginUser = async (userData) => {
    const response = await fetch('http://localhost:5000/api/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(userData),
    });
    const data = await response.json();
    return data;
  };

  return (
    <div className="login-modal">
      <h2>{isRegistering ? 'Register' : 'Login'}</h2>
      <LoginTypeSelector loginType={loginType} handleLoginTypeChange={handleLoginTypeChange} />
      <LoginForm
        isRegistering={isRegistering}
        email={email}
        setEmail={setEmail}
        password={password}
        setPassword={setPassword}
        name={name}
        setName={setName}
        course={course}
        setCourse={setCourse}
        percentile={percentile}
        setPercentile={setPercentile}
        marksheet={marksheet}
        setMarksheet={setMarksheet}
        handleFormSubmit={handleFormSubmit}
      />
      <p onClick={() => setIsRegistering(!isRegistering)}>
        {isRegistering ? 'Already have an account? Login' : 'No account? Register'}
      </p>
    </div>
  );
};

export default LoginModal;
