import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import UserInfo from './components/UserInfo';
import Filters from './components/Filters';
import CourseCard from './components/CourseCard';
import FixedBottom from './components/FixedBottom';
import './App.css';

const App = () => {
  const [user, setUser] = useState(null);
  const [courses, setCourses] = useState([]);
  const [filters, setFilters] = useState({ branches: [], districts: [] });
  const [selectedBranches, setSelectedBranches] = useState([]);
  const [selectedDistricts, setSelectedDistricts] = useState([]);
  const [cart, setCart] = useState([]);
  const [purchases, setPurchases] = useState([]);

  useEffect(() => {
    // Load user from local storage
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (storedUser) {
      setUser(storedUser);
    }

    // Fetch filters
    const fetchFilters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/filters');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setFilters(data);
      } catch (error) {
        console.error('Error fetching filters:', error);
      }
    };
    fetchFilters();
  }, []);

  useEffect(() => {
    if (user) {
      // Fetch courses based on user course
      const fetchCourses = async () => {
        try {
          const token = localStorage.getItem('token');
          const response = await fetch('http://localhost:5000/api/courses', {
            headers: {
              'Authorization': `Bearer ${token}`,
            },
          });
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          const data = await response.json();
          setCourses(data);
        } catch (error) {
          console.error('Error fetching courses:', error);
        }
      };
      fetchCourses();

      // Fetch cart
      const fetchCart = async () => {
        try {
          const token = localStorage.getItem('token');
          const response = await fetch('http://localhost:5000/api/cart', {
            headers: {
              'Authorization': `Bearer ${token}`,
            },
          });
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          const data = await response.json();
          setCart(data);
        } catch (error) {
          console.error('Error fetching cart:', error);
        }
      };
      fetchCart();

      // Fetch purchases
      const fetchPurchases = async () => {
        try {
          const token = localStorage.getItem('token');
          const response = await fetch('http://localhost:5000/api/purchases', {
            headers: {
              'Authorization': `Bearer ${token}`,
            },
          });
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          const data = await response.json();
          setPurchases(data);
        } catch (error) {
          console.error('Error fetching purchases:', error);
        }
      };
      fetchPurchases();
    }
  }, [user]);

  const handleApplyFilters = (branches, districts) => {
    setSelectedBranches(branches);
    setSelectedDistricts(districts);

    // Fetch courses with filters applied
    const fetchFilteredCourses = async () => {
      try {
        const branchQuery = branches.join(',');
        const distQuery = districts.join(',');
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:5000/api/courses?degree=${user.course}&branch=${branchQuery}&district=${distQuery}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setCourses(data);
      } catch (error) {
        console.error('Error fetching filtered courses:', error);
      }
    };
    fetchFilteredCourses();
  };

  const handleResetFilters = () => {
    setSelectedBranches([]);
    setSelectedDistricts([]);

    // Fetch courses with no filters applied
    const fetchCourses = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch(`http://localhost:5000/api/courses?degree=${user.course}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setCourses(data);
      } catch (error) {
        console.error('Error fetching courses:', error);
      }
    };
    fetchCourses();
  };

  const handleAddToCart = async (course) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/cart', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ course }),
      });
      const updatedCart = await response.json();
      setCart(updatedCart);
    } catch (error) {
      console.error('Error adding to cart:', error);
    }
  };

  const handleRemoveFromCart = async (courseId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/cart', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ courseId }),
      });
      const updatedCart = await response.json();
      setCart(updatedCart);
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  const handleBuyNow = async () => {
    try {
      const token = localStorage.getItem('token');
      await fetch('http://localhost:5000/api/checkout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ cart }),
      });
      alert('Purchase successful!');
      setCart([]); // Clear the cart after purchase
    } catch (error) {
      console.error('Error completing purchase:', error);
    }
  };

  const handleLogout = async () => {
    try {
      const token = localStorage.getItem('token');
      await fetch('http://localhost:5000/api/logout', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
      });
      localStorage.removeItem('user'); // Clear user from local storage
      localStorage.removeItem('token'); // Clear token from local storage
      setUser(null); // Clear user state
      setCart([]); // Clear cart
    } catch (error) {
      console.error('Error logging out:', error);
    }
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="app">
      <Link to="/">
        <button className="logout" onClick={handleLogout}>
          Logout
        </button>
      </Link>
      <div className="row user-info">
        <UserInfo user={user} degree={user.course} />
      </div>
      <div className="row main-content">
        <div className="filters">
          <h3 id='fixedheading'>Apply Filters</h3>
          <Filters
            branches={filters.branches}
            districts={filters.districts}
            degree={user.course}
            onApplyFilters={handleApplyFilters}
            onResetFilters={handleResetFilters}
          />
        </div>
        <div className="courses">
          {courses.map((course, index) => (
            <CourseCard
              key={index}
              course={course}
              isInCart={cart.some(item => item.id === course.id)}
              isInPurchases={purchases.some(item => item.user.id === user.id && item.course.id === course.id)}
              onBuyNow={() => handleBuyNow()}
              onAddToCart={() => handleAddToCart(course)}
              onRemoveFromCart={() => handleRemoveFromCart(course.id)}
            />
          ))}
        </div>
      </div>
      <div className="fixed-bottom">
        <FixedBottom cartCount={cart.length} />
      </div>
    </div>
  );
};

export default App;
