// src/App.js
import React, { useState, useEffect } from 'react';
import UserInfo from './components/UserInfo';
import Filters from './components/Filters';
import CourseCard from './components/CourseCard';
import FixedBottom from './components/FixedBottom';
import './App.css';

const App = () => {
  const [user, setUser] = useState(null);
  const [courses, setCourses] = useState([]);
  const [filters, setFilters] = useState({ branches: [], districts: [] });
  const [selectedBranches, setSelectedBranches] = useState([]);
  const [selectedDistrict, setSelectedDistrict] = useState('');

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/user');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error('Error fetching user:', error);
      }
    };

    const fetchFilters = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/filters');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setFilters(data);
      } catch (error) {
        console.error('Error fetching filters:', error);
      }
    };

    fetchUser();
    fetchFilters();
  }, []);

  useEffect(() => {
    if (user) {
      const fetchCourses = async () => {
        try {
          const branchParams = selectedBranches.join(',');
          const response = await fetch(`http://localhost:5000/api/courses?branches=${branchParams}&district=${selectedDistrict}`);
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          const data = await response.json();
          setCourses(data);
        } catch (error) {
          console.error('Error fetching courses:', error);
        }
      };

      fetchCourses();
    }
  }, [selectedBranches, selectedDistrict, user]);

  const handleBranchChange = (e, branch) => {
    if (e.target.checked) {
      setSelectedBranches([...selectedBranches, branch]);
    } else {
      setSelectedBranches(selectedBranches.filter(b => b !== branch));
    }
  };

  const handleDistrictChange = (e) => {
    setSelectedDistrict(e.target.value);
  };

  const handleBuyNow = (course) => {
    console.log('Buying:', course);
  };

  const handleAddToCart = (course) => {
    console.log('Adding to cart:', course);
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="app">
      <div className="row user-info">
        <UserInfo user={user} />
      </div>
      <div className="row main-content">
        <div className="filters">
          <Filters
            branches={filters.branches}
            districts={filters.districts}
            onBranchChange={handleBranchChange}
            onDistrictChange={handleDistrictChange}
          />
        </div>
        <div className="courses">
          {courses.map((course, index) => (
            <CourseCard
              key={index}
              course={course}
              onBuyNow={handleBuyNow}
              onAddToCart={handleAddToCart}
            />
          ))}
        </div>
      </div>
      <div className="fixed-bottom">
        <FixedBottom />
      </div>
    </div>
  );
};

export default App;
