// src/App.js
import React, { useState } from 'react';
import UserInfo from './components/UserInfo';
import Filters from './components/Filters';
import CourseCard from './components/CourseCard';
import FixedBottom from './components/FixedBottom';
import './App.css';

const App = () => {
  const [user, setUser] = useState({ name: 'John Doe', email: 'john.doe@example.com' });
  const [courses, setCourses] = useState([
    { title: 'Course 1', description: 'Description 1' },
    { title: 'Course 2', description: 'Description 2' },
    // Add more courses as needed
  ]);
  const [filter, setFilter] = useState('');

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
  };

  const filteredCourses = courses.filter(course =>
    course.title.toLowerCase().includes(filter.toLowerCase())
  );

  const handleBuyNow = (course) => {
    // Handle Buy Now logic
    console.log('Buying:', course);
  };

  const handleAddToCart = (course) => {
    // Handle Add to Cart logic
    console.log('Adding to cart:', course);
  };

  return (
    <div className="app">
      <div className="columns">
        <div className="column">
          <UserInfo user={user} />
        </div>
        <div className="column">
          <Filters onFilterChange={handleFilterChange} />
        </div>
        <div className="column">
          {filteredCourses.map((course, index) => (
            <CourseCard
              key={index}
              course={course}
              onBuyNow={handleBuyNow}
              onAddToCart={handleAddToCart}
            />
          ))}
        </div>
      </div>
      <FixedBottom />
    </div>
  );
};

export default App;
