// src/components/Filters.js
import React from 'react';

const Filters = ({ branches, districts, onBranchChange, onDistrictChange }) => {
  if (!branches || !districts) {
    return <div>Loading filters...</div>; // Handle the case when branches or districts are not available yet
  }

  return (
    <div>
      <h2>Apply Filters</h2>

      <div>
        <label>Branches:</label>
        {branches.map((branch, index) => (
          <div key={index}>
            <input
              type="checkbox"
              value={branch}
              onChange={(e) => onBranchChange(e, branch)}
            />
            {branch}
          </div>
        ))}
      </div>

      <div>
        <label>District:</label>
        <select onChange={onDistrictChange}>
          <option value="">All Districts</option>
          {districts.map((district, index) => (
            <option key={index} value={district}>{district}</option>
          ))}
        </select>
      </div>
    </div>
  );
};

export default Filters;
