// src/components/UserInfo.js
import React, { useState, useEffect } from 'react';
import Purchased from './Purchased';

const UserInfo = ({ user }) => {
  const [purchases, setPurchases] = useState([]);

  useEffect(() => {
    const fetchPurchases = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        console.error('No token found');
        return;
      }

      try {
        const response = await fetch('http://localhost:5000/api/purchases', {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          const errorData = await response.json();
          console.error(`HTTP error! Status: ${response.status}, Message: ${errorData.message}`);
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        setPurchases(data);
      } catch (error) {
        console.error('Error fetching purchases:', error);
      }
    };

    fetchPurchases();
  }, []); // Dependency array remains empty to run only on component mount

  return (
    <div className="user-info">
      <h2>Welcome {user.name}</h2>
      <p><strong>Degree:</strong> {user.course}</p>
      <p><strong>Email:</strong> {user.email}</p>
      <div className="purchases">
        <h3>Your Purchases</h3>
        <Purchased boughtCourses={purchases} />
      </div>
      {/* Add more user info as needed */}
    </div>
  );
};

export default UserInfo;
