// src/App.js
import React, { useState, useEffect } from 'react';
import UserInfo from './components/UserInfo';
import Filters from './components/Filters';
import CourseCard from './components/CourseCard';
import FixedBottom from './components/FixedBottom';
import './App.css';

const App = () => {
  const [user, setUser] = useState(null);
  const [courses, setCourses] = useState([]);
  //const [filter, setFilter] = useState('');
  const [filters, setFilters] = useState({ branches: [], districts: [] });
  const [selectedBranch, setSelectedBranch] = useState('');
  const [selectedDistrict, setSelectedDistrict] = useState('');

  useEffect(() => {
    fetch('/api/user')
      .then(response => response.json())
      .then(data => setUser(data));

    fetch('/api/filters')
      .then(response => response.json())
      .then(data => setFilters(data));
  }, []);

  useEffect(() => {
    if (user) {
      fetch(`/api/courses?branch=${selectedBranch}&district=${selectedDistrict}`)
        .then(response => response.json())
        .then(data => setCourses(data));
    }
  }, [selectedBranch, selectedDistrict, user]);

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
  };

  const handleBranchChange = (e) => {
    setSelectedBranch(e.target.value);
  };

  const handleDistrictChange = (e) => {
    setSelectedDistrict(e.target.value);
  };

  const handleBuyNow = (course) => {
    console.log('Buying:', course);
  };

  const handleAddToCart = (course) => {
    console.log('Adding to cart:', course);
  };

  if (!user) {
    return <div>Loading...</div>;
  }

  return (
    <div className="app">
      <div className="row user-info">
        <UserInfo user={user} />
      </div>
      <div className="row main-content">
        <div className="filters">
          <Filters
            branches={filters.branches}
            districts={filters.districts}
            onBranchChange={handleBranchChange}
            onDistrictChange={handleDistrictChange}
          />
        </div>
        <div className="courses">
          {courses.map((course, index) => (
            <CourseCard
              key={index}
              course={course}
              onBuyNow={handleBuyNow}
              onAddToCart={handleAddToCart}
            />
          ))}
        </div>
      </div>
      <div className="fixed-bottom">
        <FixedBottom />
      </div>
    </div>
  );
};

export default App;
