import React, { useEffect, useState } from 'react';

const CourseList = () => {
    const [courses, setCourses] = useState([]);
    const [updatedCourses, setUpdatedCourses] = useState({});

    useEffect(() => {
        fetch('http://localhost:5000/api/courses-admin')
            .then(res => res.json())
            .then(data => setCourses(data))
            .catch(err => console.error(err));
    }, []);

    const handleStatusChange = (id, status) => {
        setUpdatedCourses(prevState => ({
            ...prevState,
            [id]: status,
        }));
    };

    const handleSubmit = () => {
        const coursesToUpdate = courses.map(course => ({
            ...course,
            status: updatedCourses[course.id] || course.status,
        }));

        fetch('http://localhost:5000/api/courses-admin', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(coursesToUpdate),
        })
        .then(res => res.text())
        .then(data => {
            setCourses(coursesToUpdate);
            alert(data);
        })
        .catch(err => console.error(err));
    };

    return (
        <div className="course-list">
            {courses.map(course => (
                <div key={course.id}>
                    <h3>{course.title}</h3>
                    <p><b>{course.branch}</b></p>
                    <p>{course.description}</p>
                    <label>
                        <input
                            type="radio"
                            name={`status-${course.id}`}
                            value="verified"
                            checked={
                                updatedCourses[course.id] === 'verified' ||
                                (course.status === 'verified' && !updatedCourses[course.id])
                            }
                            onChange={() => handleStatusChange(course.id, 'verified')}
                        />
                        Verified
                    </label>
                    <label>
                        <input
                            type="radio"
                            name={`status-${course.id}`}
                            value="unverified"
                            checked={
                                updatedCourses[course.id] === 'unverified' ||
                                (course.status === 'unverified' && !updatedCourses[course.id])
                            }
                            onChange={() => handleStatusChange(course.id, 'unverified')}
                        />
                        Unverified
                    </label>
                </div>
            ))}
            <button onClick={handleSubmit}>Submit</button>
        </div>
    );
};

export default CourseList;
