import React, { useState, useEffect } from 'react';

const StudentDetails = () => {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    const fetchStudentUsers = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/student-users');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setStudents(data);
      } catch (error) {
        console.error('Error fetching student users:', error);
      }
    };

    fetchStudentUsers();
  }, []);

  return (
    <div>
      <h2>Student Users</h2>
      {students.map((student) => (
        <div key={student._id}>
          <h3>{student.user.name}</h3>
          <p>Email: {student.user.email}</p>
          <p>Courses Purchased:</p>
          <ul>
            {student.purchases.map(purchase => (
              <li key={purchase._id}>
                {purchase.course.title} - {purchase.course.branch} (Cost: {purchase.course.cost} Rs, Payment: {purchase.invoice})
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default StudentDetails;
