import React, { useState, useEffect } from 'react';
import Purchased from './Purchased.js';
import BillingPage from './BillingPage'; // Import the BillingPage component

const UserInfo = ({ user }) => {
  const [purchases, setPurchases] = useState([]);

  const fetchPurchases = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/purchases');
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const data = await response.json();
      setPurchases(data);
    } catch (error) {
      console.error('Error fetching purchases:', error);
    }
  };

  useEffect(() => {
    fetchPurchases();
  }, []);

  return (
    <div>
      <h2>Welcome {user.name}</h2>
      <p>Degree: {user.course}</p>
      <p>Email: {user.email}</p>
      <div className="purchases">
        <Purchased
          boughtCourses={purchases}
        />
      </div>
      <BillingPage onPurchase={fetchPurchases} /> {/* Add the BillingPage component with callback */}
    </div>
  );
};

export default UserInfo;
