import React, { useState, useEffect } from 'react';
import { Link } from "react-router-dom";
import '../billing.css';

const BillingPage = () => {
  const [cart, setCart] = useState([]);
  const [showBreakdown, setShowBreakdown] = useState(false);
  const platformFee = 50;

  // Calculate total cost, GST, and the overall total cost
  const totalCost = cart.reduce((total, course) => total + course.cost, 0);
  const gst = (12 / 100) * totalCost;
  const totalAmount = totalCost + platformFee + gst;

  // Fetch cart data on component mount
  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/cart');
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        const data = await response.json();
        setCart(data);
      } catch (error) {
        console.error('Error fetching cart:', error);
      }
    };
    fetchCart();
  }, []); // Empty dependency array ensures this runs once on mount
  
  // Remove course from cart
  const handleRemoveFromCart = async (courseId) => {
    try {
      const response = await fetch('http://localhost:5000/api/cart', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ courseId }),
      });
      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
      const updatedCart = await response.json();
      setCart(updatedCart);
    } catch (error) {
      console.error('Error removing from cart:', error);
    }
  };

  // Handle Buy Now action
  const handleBuyNow = async () => {
    const numCourses = cart.length;

    let invoice;
    if (numCourses === 0) {
      invoice = `paid ${totalAmount} for this course on ${new Date().toLocaleDateString()}`;
    } else if (numCourses === 1) {
      invoice = `paid ${totalAmount} for this course and 1 other on ${new Date().toLocaleDateString()}`;
    } else {
      invoice = `paid ${totalAmount} for this course and ${numCourses - 1} others on ${new Date().toLocaleDateString()}`;
    }

    // Assuming `user` is available via context or props
    const user = {
      id: 'USER_ID', // Replace with actual user ID
      name: 'USER_NAME', // Replace with actual user name
      email: 'USER_EMAIL', // Replace with actual user email
    };

    const courses = cart.map(course => ({
      courseId: course.id,
      title: course.title,
      cost: course.cost
    }));

    try {
      const response = await fetch('http://localhost:5000/api/purchasesp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ user, courses, invoice }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      alert('Purchase successful!');
      setCart([]); // Clear the cart after purchase
    } catch (error) {
      console.error('Error making purchase:', error);
    }
  };

  // Toggle breakdown view
  const handleBreakdown = () => {
    setShowBreakdown(!showBreakdown);
  };

  return (
    <div className="billing-page">
      <h2 className="billing-page-title">Your Cart</h2>
      <div className="cart-items">
        {cart.length > 0 ? (
          cart.map((course) => (
            <div key={course.id} className="cart-item">
              <span className="cart-item-title">{course.title}</span>
              <span className="cart-item-id">Course ID: {course.id}</span>
              <span className="cart-item-cost">{course.cost} Rs</span>
              <button className="remove-btn" onClick={() => handleRemoveFromCart(course.id)}>Remove</button>
            </div>
          ))
        ) : (
          <p className="empty-cart-message">Your cart is empty</p>
        )}
      </div>
      <div className="billing-summary">
        <h3 className="total-cost">Total: {totalAmount} Rs</h3>
        <button className="breakdown-btn" onClick={handleBreakdown} disabled={cart.length === 0}>
          Detailed Bill
        </button>
        <button className="buy-now-btn" onClick={handleBuyNow} disabled={cart.length === 0}>
          Buy Now
        </button>
        <Link to="/dashboard">
          <button className="go-to-dashboard">
            Back to Dashboard
          </button>
        </Link>
      </div>

      {showBreakdown && (
        <div className="breakdown-popup">
          <div className="breakdown-content">
            <h3>Cost Breakdown</h3>
            <p>Total Cost: {totalCost} Rs</p>
            <p>Platform Fee: {platformFee} Rs</p>
            <p>Tax: {gst.toFixed(2)} Rs</p>
            <button className="close-popup-btn" onClick={handleBreakdown}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default BillingPage;
